public class Product : AggregateRoot<ProductId>
{
    public string Name { get; private set; }
    public Money Price { get; private set; }
    private readonly List<ProductVariant> _variants = new();
    public IReadOnlyCollection<ProductVariant> Variants => _variants.AsReadOnly();

    // .NET 9 / C# 12 Primary Constructor usage in static factory
    public static Product Create(string name, Money price)
    {
        var product = new Product { Id = new ProductId(Guid.NewGuid()), Name = name, Price = price };
        product.AddDomainEvent(new ProductCreatedEvent(product.Id));
        return product;
    }
}

public class GetProductDetailsModel(IProductService productService) : PageModel
{
    [BindProperty(SupportsGet = true)]
    public Guid Id { get; set; }
    public ProductDto Product { get; set; }

    public async Task OnGetAsync()
    {
        Product = await productService.GetByIdAsync(Id);
    }
}

public class ProductConfiguration : IEntityTypeConfiguration<Product>
{
    public void Configure(EntityTypeBuilder<Product> builder)
    {
        builder.HasKey(p => p.Id);
        builder.Property(p => p.Id).HasConversion(id => id.Value, value => new ProductId(value));
        builder.OwnsOne(p => p.Price, price => 
        {
            price.Property(m => m.Amount).HasColumnName("PriceAmount");
            price.Property(m => m.Currency).HasColumnName("PriceCurrency").HasMaxLength(3);
        });
    }
}
